const modal=document.querySelector(".modal"),btn=document.querySelector(".openPopupButton"),span=document.querySelector(".close"),popupText=document.querySelector(".popupText");btn.onclick=function(){modal.style.display="block";var e=localStorage.getItem("numberOfClicks");null==e?localStorage.setItem("numberOfClicks",1):localStorage.setItem("numberOfClicks",parseInt(e)+1),popupText.innerHTML="You have clicked "+localStorage.getItem("numberOfClicks")+" times to related button."},span.onclick=function(){modal.style.display="none"},window.onclick=function(e){e.target==modal&&(modal.style.display="none")};